const {add,sub,mul,div} = require('./maths')

const calc = function(type,...params){
    let ans=0;
    switch(type){
        case 'add':
            for(let x of params){
                ans = add(ans,x)
            }
            break;
        case 'sub':
            ans = sub(params[0],params[1])
            break;
        case 'mul':
            ans=1;
            for(let x of params){
                ans = mul(ans,x)
            }
            break;
        case 'div':
            ans = div(params[0],params[1])
            break;
        default:
            throw console.error();
    }
    return ans;
    
}

module.exports.firstmathlab = calc